A=(0,1.155);
B=(0,0);
C=(-1,1.732);
D=(1,1.732);
draw(arc((-2,0),2,0,60));
draw(arc((0,3.464),2,-60,-120));
draw(arc((2,0),2,120,180));
dot(A);
label("A", A, N);
draw(A--B);
draw(A--C);
draw(A--D);
[/asy] Now consider a circle of radius 4 inscribed inside a regular hexagon:
[asy]
size(150);
pair O, A, B, C, D, E, F, M;
O=(0,0);
A=(-4.619,0);
B=(-2.309,4);
C=(2.309,4);
D=(4.619,0);
E=(2.309,-4);
F=(-2.309,-4);
M=(A+B)/2;
draw(circle(O,4));
draw(A--B--C--D--E--F--A);
